				<ul class="sub-menu">
					<li data-cat="all" class="current loaded"><a href="#"><?php echo sprintf( _x( 'All %s', 'Sub menu item of Mews Mega menu ', 'yeahthemes' ), $title );?></a></li>
					<?php
					
					foreach ( $category_list as $cat_id ) {
						//print_r($this_cat);
						if( get_cat_name( $cat_id ) ):
						?>
							<li data-cat="<?php echo esc_attr( $cat_id );?>" class="loaded"><a href="<?php echo esc_attr( get_category_link( $cat_id ) );?>" title="<?php echo esc_attr( get_cat_name( $cat_id ) );?>"><?php echo get_cat_name( $cat_id );?></a></li>
						<?php
						endif;
					}
				?></ul>
			</li>
			<?php endif;

			$myposts = get_posts( apply_filters( 'yt_site_ajax_mega_menu_by_category_query', array( 
				'posts_per_page' => 5, 
				'cat' => $data_cat,
				) )
			);

			foreach ( $myposts as $post ) :
				setup_postdata( $post ); 

				static $count = 0;
				$count++;

				$post_cats = get_the_category(get_the_ID());
				$cat_tag_args = array();
				foreach ( $post_cats as $cat ) {
					if ( in_array( $cat->cat_ID, $category_list )) {
						$cat_tag_args['cat_ID'] = $cat->cat_ID;
						$cat_tag_args['name'] = $cat->name;
						$cat_tag_args['slug'] = $cat->slug;
						//echo 'Break';
						break;
					}
				}
				?>
				<li data-filter="all" style="<?php /*echo 'animation-delay:' . ($count * .1) .'s;-webkit-animation-delay:' . ($count* .1) .'s;';*/?>" class="post-with-large-thumbnail <?php echo 5 == $count || 4 == $count ? 'hidden-md ' : ''; ?>col-md-3 col-lg-2">
