						<?php if ( has_post_thumbnail() && get_the_post_thumbnail() && ! post_password_required() ) : ?>
						<div class="post-thumb large">
							<span class="cat-tag <?php echo esc_attr($cat_tag_args['slug'] );?>"><?php echo $cat_tag_args['name'];?></span>
							<a href="<?php the_permalink(); ?>" rel="bookmark" title="<?php esc_attr( get_the_title() ); ?>"><?php the_post_thumbnail('medium'); ?></a>
						</div>
						<?php endif;?>
						
						<time class="entry-date published" datetime="<?php echo esc_attr( get_the_time('c') ); ?>"><?php the_time('M j, Y') ;?></time>
						<h3 class="secondary-2-primary no-margin-bottom"><a href="<?php the_permalink();?>" rel="bookmark"><?php the_title();?></a></h3>
						
					</article>
				</li>

				<?php

			endforeach;

			wp_reset_postdata();

			//Get the post from individual category 

			foreach ( $category_list as $cat_id ) :


				$myposts = get_posts( apply_filters( 'yt_site_ajax_sub_mega_menu_by_category_query', array( 
					'posts_per_page' => 5, 
					'cat' => $cat_id,
					) )
				);
				
				foreach ( $myposts as $post ) :
					setup_postdata( $post ); 

					static $count = 0;
					$count++;

					$post_cat = get_category( $cat_id );
					$cat_tag_args = array();

					$cat_tag_args['cat_ID'] = $post_cat->cat_ID;
					$cat_tag_args['name'] = $post_cat->name;
					$cat_tag_args['slug'] = $post_cat->slug;
					?>
					<li data-filter="<?php echo esc_attr( $cat_id );?>" style="display:none;" class="post-with-large-thumbnail <?php echo 5 == $count || 4 == $count ? 'hidden-md ' : ''; ?>col-md-3 col-lg-2">
						<article <?php post_class();?>>
							<?php if ( has_post_thumbnail() && get_the_post_thumbnail() && ! post_password_required() ) : ?>
							<div class="post-thumb large">
								<a href="<?php the_permalink(); ?>" rel="bookmark" title="<?php esc_attr( get_the_title() ); ?>"><?php the_post_thumbnail('medium'); ?></a>
							</div>
							<?php endif;?>
							
							<time class="entry-date published" datetime="<?php echo esc_attr( get_the_time('c') ); ?>"><?php the_time('M j, Y') ;?></time>
							<h3 class="secondary-2-primary no-margin-bottom"><a href="<?php the_permalink();?>" rel="bookmark"><?php the_title();?></a></h3>
							
						</article>
					</li>

					<?php

				endforeach;

				wp_reset_postdata();
			endforeach;
			//print_r(get_the_category(1));

		echo '</ul>';
		//print_r(get_the_category(1));
		
		$post = $post_backup;
		
		die(1);
    }
}

