if ( ! function_exists( 'yt_site_social_networks' ) ) {
	 
	function yt_site_social_networks( $return = true){
		
		$yt_data = yt_get_options();

		$network_array = apply_filters( 'yt_site_social_networks_args', array( 
			'facebook' => __('Facebook', 'yeahthemes'), 
			'twitter' => __('Twitter', 'yeahthemes'), 
			'googleplus' => __('Google+', 'yeahthemes'), 
			'youtube' => __('Youtube', 'yeahthemes'), 
			'email' => __('Send an Email', 'yeahthemes'), 
			'rss' => __('RSS Feed', 'yeahthemes')
		) );

		$network_icon_array = apply_filters( 'yt_site_social_networks_icon_args', array( 
			'facebook' => 'fa fa-facebook', 
			'twitter' => 'fa fa-twitter', 
			'googleplus' => 'fa fa-google-plus', 
			'youtube' => 'fa fa-youtube', 
			'email' => 'fa fa-envelope', 
			'rss' => 'fa fa-rss', 
		) );
		
		$social_networks ='';

		foreach( $network_array as $network	 => $name ){

			if( !empty( $yt_data["scl_$network"] ) ){
				if( 'email' == $network ){
					$value = 'mailto:' . antispambot( $yt_data["scl_$network"] );
				}else{
					$value = esc_url( $yt_data["scl_$network"] );
				}
				$social_networks .= sprintf('<a href="%s" target="_blank" title="%s"><i class="%s"></i></a>',
					$value  ,
					$name,
					esc_attr( $network_icon_array[$network] )
				);
			}
		}

		if( $return  )
			return $social_networks;
		else
			echo $social_networks;
	}
}

