if ( ! function_exists( 'yt_site_archive_header' ) ) {
	
	function yt_site_archive_header(){
		if(!is_archive())
			return;

		if( yt_is_woocommerce() )
			return;

		if( yt_is_bbpress() )
			return;
		
		?>
		<header class="page-header archive-title hidden-print">
			<h1 class="page-title">
				<?php
					if ( is_category() ) :
						single_cat_title();

					elseif ( is_tag() ) :
						_e('Tag: ', 'framework');
						single_tag_title();

					elseif ( is_author() ) :
						/* Queue the first post, that way we know
