if ( ! function_exists( 'yt_site_featured_news' ) ) {
	
	function yt_site_featured_news( $output_data = 'html' ){

		if( !is_page() && !is_home() && !is_front_page() && !yt_is_woocommerce() )
			return;

		$paged = (get_query_var('paged')) ? get_query_var('paged') : 1;

		global $post;
		/*Backup global post*/
		$backup_post = $post;

		$post_id = !empty( $post->ID ) ? $post->ID : 0;

		if( is_home() ){
			$post_id = get_option( 'page_for_posts' );
		}

		if( yt_is_woocommerce() && is_shop() && wc_get_page_id('shop') )
			$post_id = wc_get_page_id('shop');

		/*Allow filtering to show site hero on paged page for post*/
		if( is_paged() && !apply_filters( 'yt_site_hero_banner_allow_on_paged', false, $post_id ) ){
			return;
		}

		$hero_mode = get_post_meta( $post_id, 'yt_page_herobanner_mode', true  );
		
		if( !$hero_mode )
			return;

		$hero_layout 	= get_post_meta( $post_id, 'yt_page_herobanner_layout', true );
		$hero_style 	= get_post_meta( $post_id, 'yt_page_herobanner_style', true );
		$hero_effect	= get_post_meta( $post_id, 'yt_page_herobanner_effect', true );
		$hero_style_random = get_post_meta( $post_id, 'yt_page_herobanner_style_random', true );
		$categoryin 	= get_post_meta( $post_id, 'yt_page_herobanner_category', true  );
		$order 			= get_post_meta( $post_id, 'yt_page_herobanner_order', true );
		$orderby 		= get_post_meta( $post_id, 'yt_page_herobanner_orderby', true );
		$postsperpage 	= get_post_meta( $post_id, 'yt_page_herobanner_postsperpage', true );
		$postin 		= get_post_meta( $post_id, 'yt_page_herobanner_postin', true );
		$tagin 			= get_post_meta( $post_id, 'yt_page_herobanner_tagin', true );
		$exclude_format	= get_post_meta( $post_id, 'yt_page_herobanner_excludeformat', true );

		/*Asign post number*/ 
		$postsperpage = !empty( $postsperpage  ) && $postsperpage > 4 ? $postsperpage  : 8;

		if( 'default' == $hero_layout ){
			$postsperpage = 5;
		}
		elseif( 'symmetry_brick' == $hero_layout ){
			$postsperpage = 6;
		}

		/*Class style*/
		$hero_class = $hero_layout;
		if( 'color' == $hero_style ){
			$hero_class .= ' style-color-gradient';
		}elseif( 'mixed' == $hero_style ){
			$hero_class .= ' style-mixed-color-gradient';
		}
		/*overlay color*/
		$overlay_color = array( 
			'shamrock-skyblue', 
			'cerise-yellow',
			'yellow-skyblue',
			'shamrock-yellow',
			'waikawagray-cerise',
			'pictonblue-purple'
		);
		/*If is randon , do shuffling an array*/
		if( 'mixed' == $hero_style && $hero_style_random ){			
			shuffle( $overlay_color );
		}

		/*Begin query*/ 
		$hero_brick_query_args = array(
			'order' 			=> $order ? $order : 'DESC',
			'orderby' 			=> $orderby ? $orderby : 'date',
			'posts_per_page' 	=> intval( $postsperpage ),
			'post_type' 		=> 'post',
			'post_status' 		=> 'publish',
			'ignore_sticky_posts' => 1,
		);

		/* Push category to query*/
		if( !empty( $categoryin ) && is_array( $categoryin ) )
			$hero_brick_query_args['cat'] = join( ',', $categoryin );

		/* Push Specified post to query*/
		if( !empty( $postin ) && is_array( $postin ) )
			$hero_brick_query_args['post__in'] = $postin;

		/* Push Specified post to query*/
		if( !empty( $tagin ) && is_array( $tagin ) )
			$hero_brick_query_args['tag__in'] = $tagin;

		/* Push Specified post to query*/
		if( !empty( $exclude_format ) && is_array( $exclude_format ) ){
			$exclude_format_temp = array();
			foreach( $exclude_format as $format ){
				$exclude_format_temp[] = "post-format-$format";
			}

			$hero_brick_query_args['tax_query'] = array(
			    array(
			      'taxonomy' 	=> 'post_format',
			      'field' 		=> 'slug',
			      'terms' 		=> $exclude_format_temp,
			      'operator' 	=> 'NOT IN'
			    )
			);
		}
		/*Allow ordering posts by post views*/ 
		if( 'meta_value_num' == $orderby ){
			$hero_brick_query_args['meta_key'] = apply_filters( 'yt_simple_post_views_tracker_meta_key', '_post_views' );
			$hero_brick_query_args['meta_value_num'] = '0';
			$hero_brick_query_args['meta_compare'] = '>';
		}

		$slider_settings = apply_filters( 'yt_site_hero_banner_carousel_settings', array(
			'selector' => '.slides > li',
			//'directionNav' => false,
			'randomize' => 'true',
			'pausePlay' => false,
			'animationLoop' => false
		) , 'carousel' );


		$hero_post_ids = array();
		
		// print_r($x);
		// die();
		//$hero_query->post_count)

		//print_r($hero_brick_query_args); die();

		/*Init the query, and allow filtering*/
	/*If html == $output_data*/
	if( $output_data == 'html' ):

		$hero_query = new WP_Query( apply_filters( 'yt_site_hero_banner_query', $hero_brick_query_args, $post_id ) );

		if ( $hero_query->have_posts() && $hero_query->post_count >= $postsperpage ) {
			/*Begin site hero*/ 
			echo '<div id="site-hero" class="site-hero ' . esc_attr( $hero_class ) . ' border-bottom clearfix hidden-print yt-loading fadeIn animated" data-layout="'.esc_attr( $hero_layout ).'" data-style="'.esc_attr( $hero_style ).'" data-effect="'.esc_attr( $hero_effect ).'">
				<div class="container">
					<div class="row">';
				/*Slider wrapper for carousel*/
				if( 'carousel' == $hero_layout ){
					echo '<div class="yeahslider" data-settings="' . esc_attr( json_encode( $slider_settings ) ) . '">
						<ul class="slides">';
				}
			$count = 0;
			/*Start the Loop*/ 
			while ( $hero_query->have_posts() ) {
				$hero_query->the_post();
				$count++;
				/*Push the id to array*/
				$hero_post_ids[] = $hero_query->post->ID;

				if( $count > $postsperpage )
					break;

				$class 				= '';
				$thumb_size 		= 'medium';
				$thumb_holder_size 	= '320x190';
				$categories 		= get_the_category();
				$cat_tag 			= '';
				$column_size		= 'col-md-6 ';

				
				if( ( 1 == $count && 'default' == $hero_layout ) || ( in_array( $count, array( 1, 6 ) ) && 'symmetry_brick' == $hero_layout ) ){
					$class 			= ' hero-brick-large';
					$thumb_size 	= 'post-thumbnail';
					$thumb_holder_size = '670x410';
				}
				/*Category tag*/
				if( !empty( $categories[0] ) && apply_filters( 'yt_site_hero_element_cat', true, $post_id ) ){
					$category 	= $categories[0];
					$cat_tag  	.= '<span class="entry-category">';
					$cat_tag 	.= '<a class="cat-tag ' . esc_attr( $category->slug ) . '" href="'. esc_attr( get_category_link( $category->term_id ) ) .'" title="' . esc_attr( sprintf( __( 'View all posts in %s', 'yeahthemes' ), $category->name ) ) . '">'.$category->cat_name.'</a>';
					$cat_tag 	.= '</span>';
					//$class 		.= ' category-' . $category->slug;
				}
				/*Symmetric large brick*/
				if( in_array( $count, array( 1, 6 ) ) && 'symmetry_brick' == $hero_layout ){
					$column_size = 'col-md-12 ';
				}
				/*Carousel brick*/
				if( 'carousel' == $hero_layout ){
					$column_size = 'col-sm-6 col-md-3 ';
					if( 1 == $count )
						echo '<li>';
				}
				/*Symmetric & default layout*/
				if( in_array( $hero_layout, array( 'symmetry_brick', 'default' ) ) ){
					if( in_array( $count, array( 2, 3, 4, 5 ) ) )
						$column_size = 'col-sm-6 ' . $column_size;
					if( 6 == $count )
						$column_size = 'hidden-sm ' . $column_size;
				}

				$article_class = $column_size . 'hero-brick' . $class . ' clearfix visibility-hidden';

				if( ( 2 == $count && 'default' == $hero_layout ) || ( in_array( $count, array( 1, 4 ) ) && 'symmetry_brick' == $hero_layout ) ){
					echo '<div class="col-md-6">
					<div class="row">';
				}

					echo '<article class="' . esc_attr( join( ' ', get_post_class( $article_class ) ) ) . '">
						<header class="entry-header">
							' . $cat_tag . '
							<h2 class="entry-title"><a href="' . esc_url( get_permalink() ) . '" title="' . esc_attr( get_the_title() ) . '">' . get_the_title() . '</a></h2>
						</header>
						<div class="entry-content">
							<a href="' . esc_url( get_permalink() ) . '" class="entry-thumbnail' . ( 'mixed' == $hero_style ? esc_attr( ' goverlay-' . $overlay_color[ $count-1 ] ) : '' ) . '">';
							if( !has_post_thumbnail())
								echo '<img src="data:image/gif;base64,">';
							else{
								
								the_post_thumbnail( $thumb_size );
							}
							echo '</a>
						</div>
						<footer class="entry-meta">
							<span class="pull-left posted-on"><i class="fa fa-clock-o"></i> <time class="entry-date published" datetime="' . esc_attr( get_the_time('c') ) . '">' . get_the_time('M j, Y') . '</time></span>
							<span class="pull-right with-cmt"><i class="fa fa-comments"></i> ';
							
							comments_popup_link( __( '0', 'yeahthemes' ), __( '1', 'yeahthemes' ), __( '%', 'yeahthemes' ));
							
							echo '</span>
						</footer>
					</article>';

				if( ( 5 == $count && 'default' == $hero_layout ) || ( in_array( $count, array( 3, 6 ) ) && 'symmetry_brick' == $hero_layout ) ){
					echo '</div>
					</div>';
				}
				/*Carousel brick*/
				if( 'carousel' == $hero_layout ){
					
					if( 4 == $count ){
						echo '</li>';
						$count = 0;
					}
				}
			
			}/*End the loop*/

				/* End slider wrapper for Carousel */
				if( 'carousel' == $hero_layout ){
					echo '</ul>
						</div>';
				}

			/*End site hero*/ 
				echo '</div>
				</div>
			</div>';
		} else {
			// no posts found
			echo '<div id="site-hero" class="site-hero border-bottom clearfix hidden-print yt-loading fadeIn animated text-center"><h3>' . __('You need to add more than 5 posts so the hero banner can show blocks properly', 'yeahthemes') . '</h3></div>';
		}
		//print_r( $hero_post_ids);

	else:
		$hero_brick_query_args['fields'] = 'ids';
		$hero_post_ids = get_posts( $hero_brick_query_args );
		//print_r( $hero_post_ids);
	endif;

		wp_reset_postdata();
		$post = $backup_post;

	/*Return hero ids*/
	if( $output_data !== 'html' )
		return $hero_post_ids;

	}
}

