if ( ! function_exists( 'yt_site_single_post_related_articles' ) ) {
	 
	function yt_site_single_post_related_articles(){

		if( !is_singular( 'post' ))
			return;

		if( 'hide' == yt_get_options( 'blog_single_post_related_articles') )
			return;

		global $post;

		$backup_post = $post;

		if( !empty( $post )){
			$post_id = $post->ID;
		}else{
			$post_id = 0;
		}

		$tags = wp_get_post_tags( $post_id );
		if ($tags):

			$tag_ids = array();  
			foreach( $tags as $tag )
				$tag_ids[] = $tag->term_id; 

			$args = array(  
				'tag__in' => $tag_ids,  
				'post__not_in' => array( $post_id ),  
				'posts_per_page'=> 5 //number of posts to display
			);       
			$related_articles = get_posts( apply_filters( 'yt_site_single_post_related_articles_query', $args, $post_id ) );
			if( $related_articles ):?>
			<div class="entry-stuff margin-bottom-30 related-articles hidden-print secondary-2-primary">
				<h3 class="related-articles-title"><?php _e( 'Related Articles', 'yeahthemes'); ?></h3>
				<ul class="list-unstyled list-with-separator">
				<?php
				foreach ( $related_articles as $post ) : 
	  				setup_postdata( $post ); ?>
					<li><a href="<?php the_permalink();?>"><i class="fa fa-file-text-o "></i> <?php echo  the_title();?> - <time class="entry-date published" datetime="<?php echo esc_attr( get_the_time('c') ); ?>"><?php echo the_time('M j, Y');?></time></a></li>
	  			<?php endforeach;?>

				</ul>
			</div>
	  		<?php
  			endif;
			wp_reset_postdata();
		endif;

		$post = $backup_post;
	}
}


/**
