if ( ! function_exists( 'yt_site_single_post_you_might_also_like' ) ) {
	 
	function yt_site_single_post_you_might_also_like(){

		global $post;

		$backup_post = $post;

		if( empty( $post ) && !is_single() )
			return;

		if( 'hide' == yt_get_options( 'blog_single_post_youmightalsolike') )
			return;

		/*Get current post's categories*/
		$post_cats_temp = get_the_category( $post->ID );
		$post_cats = array();
		if( !empty( $post_cats_temp )){
			foreach ( $post_cats_temp as $cat) {
				$post_cats[] = $cat->cat_ID;
			}
		}

		if( empty( $post_cats ) )
			return;


		global $wpdb;
		// $querystr = "
		// 	SELECT $wpdb->posts . * 
		// 	FROM $wpdb->posts, $wpdb->postmeta 
		// 	WHERE $wpdb->posts.ID = $wpdb->postmeta.post_id 
		// 	AND $wpdb->postmeta.meta_key = '_post_views' 
		// 	AND $wpdb->postmeta.meta_value > 1 
		// 	AND $wpdb->posts.post_status = 'publish' 
		// 	AND $wpdb->posts.post_type = 'post' 
		// 	ORDER BY $wpdb->postmeta.meta_value DESC LIMIT 4
		// ";

		// foreach ($wpdb->get_results(  $querystr ) as $key) {

		// 	echo "$key->post_title<br>";
			
		// }

		/*Prepare the query, allow modifying by applying filter*/
		$myposts = get_posts( apply_filters( 'yt_site_single_post_you_might_also_like_query', array( 
			'order' 			=> 'DESC',
			'orderby' 			=> 'title date',
			'post_type' 		=> 'post',
			'post_status' 		=> 'publish',
			'post__not_in'		=> array( $post->ID ),
			'posts_per_page' => 4, 
			'category__in' => $post_cats,
			'tax_query' => array(
				array(
					'taxonomy' 	=> 'post_format',
					'field' 	=> 'slug',
					'terms' 	=> array( 'post-format-image', 'post-format-link', 'post-format-quote'),
					'operator' 	=> 'NOT IN'
				)
			)
		) ) );

		if ( $myposts ) :
			echo '<div class="entry-stuff secondary-2-primary you-might-also-like-articles hidden-print margin-bottom-30">';
				echo sprintf( '<h3>%s</h3>', __( 'You Might Also Like', 'yeahthemes' ) );
				echo '<ul class="post-list post-list-with-thumbnail horizontal row">';
				foreach ( $myposts as $post ) :
					setup_postdata( $post );

					static $count = 0;
					$count++;

					$categories = get_the_category();
					$cat_tag 			= '';
			
					if( !empty( $categories[0] ) ){
						$category 	= $categories[0];
						$cat_tag 	.= '<span class="cat-tag ' . esc_attr( $category->slug ) . '">'.$category->cat_name.'</span>';
					}
					?>
					<li class="post-with-large-thumbnail col-xs-6 col-sm-6 col-md-4 margin-bottom-15<?php echo 4 == count( $myposts ) && 4 == $count ? ' hidden-md hidden-lg' : ''; ?>">
						<article <?php post_class();?>>
							<?php if( has_post_thumbnail() && get_the_post_thumbnail() && ! post_password_required() ): ?>
							<div class="post-thumb large">
								<?php echo $cat_tag;?>
								<a href="<?php the_permalink(); ?>" rel="bookmark" title="<?php esc_attr( get_the_title() ); ?>"><?php the_post_thumbnail('medium'); ?></a>
							</div>
							<?php endif;?>
							
							<time class="entry-date published" datetime="<?php echo esc_attr( get_the_time('c') ); ?>"><?php the_time('M j, Y');?></time>
							<h2><a href="<?php echo esc_attr( get_permalink() );?>" rel="bookmark"><?php the_title();?></a></h2>
							
						</article>
					</li>
					<?php

				endforeach;
				echo '</ul>';
			echo '</div>';
		endif;

		/*Reset post data & restore global post*/
		wp_reset_postdata();
		
		/*Restore post*/
		$post = $backup_post;
	}
}

